package procode;

public class printf_2_42_pro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean my_bolean= true;
		char my_char = 'H';
		String my_string = "bro";
		int my_int = 39;
		double my_double = 1000000;
		
		System.out.printf("%b is a boolean",my_bolean);
		System.out.printf("%c is a char",my_char);
		System.out.printf("%d is a int ",my_int);
		System.out.printf("%s is a string",my_string);
		System.out.printf("%f is a double",my_double);
//		you can add to these b,c,d,s,f some of this -,+,0,, 
//		like this (%,f) this will add a comma to the sausands 
		
		

	}

}
